pub fn new() -> Result<Self> {
        // Check for API key first (takes precedence)
        if let Ok(api_key) = std::env::var("ANTHROPIC_API_KEY") {
            return Self::from_api_key_with_mode(&api_key, AuthMode::ApiKey);
        }

        // Fall back to OAuth token
        if let Ok(oauth_token) = std::env::var("CLAUDE_CODE_OAUTH_TOKEN") {
            return Self::from_api_key_with_mode(&oauth_token, AuthMode::OAuth);
        }

        Err(anyhow!(
            "No API key found. Set ANTHROPIC_API_KEY or CLAUDE_CODE_OAUTH_TOKEN environment variable"
        ))
    }

pub fn from_api_key(api_key: &str) -> Result<Self> {
        Self::from_api_key_with_mode(api_key, AuthMode::ApiKey)
    }

pub fn from_api_key_with_mode(api_key: &str, auth_mode: AuthMode) -> Result<Self> {
        if api_key.is_empty() {
            return Err(anyhow!("API key cannot be empty"));
        }

        Ok(Self {
            api_key: api_key.to_string(),
            model: DEFAULT_MODEL.to_string(),
            client: Client::new(),
            auth_mode,
        })
    }

pub fn is_oauth_mode(&self) -> bool {
        self.auth_mode == AuthMode::OAuth
    }

pub fn get_system_prompt_prefix(&self) -> &'static str {
        CLAUDE_CODE_PROMPT_PREFIX
    }

pub fn get_anthropic_beta_header(&self) -> &'static str {
        ANTHROPIC_BETA_HEADER
    }

fn get_api_url(&self) -> String {
        if self.auth_mode == AuthMode::OAuth {
            format!("{}?beta=true", API_URL)
        } else {
            API_URL.to_string()
        }
    }

fn add_auth_headers(&self, builder: reqwest::RequestBuilder) -> reqwest::RequestBuilder {
        match self.auth_mode {
            AuthMode::ApiKey => builder.header("x-api-key", &self.api_key),
            AuthMode::OAuth => builder
                .header("Authorization", format!("Bearer {}", self.api_key))
                .header("anthropic-beta", ANTHROPIC_BETA_HEADER),
        }
    }

async fn send_api_request<T: Serialize>(&self, request: &T) -> Result<String> {
        let request_builder = self
            .client
            .post(self.get_api_url())
            .header("anthropic-version", API_VERSION)
            .header("content-type", "application/json");

        let response = self
            .add_auth_headers(request_builder)
            .json(request)
            .send()
            .await
            .map_err(|e| anyhow!("Failed to send request: {}", e))?;

        let status = response.status();
        let body = response
            .text()
            .await
            .map_err(|e| anyhow!("Failed to read response body: {}", e))?;

        if !status.is_success() {
            if let Ok(error) = serde_json::from_str::<AnthropicError>(&body) {
                return Err(anyhow!(
                    "API error ({}): {} - {}",
                    status.as_u16(),
                    error.error.error_type,
                    error.error.message
                ));
            }
            return Err(anyhow!("API error ({}): {}", status.as_u16(), body));
        }

        Ok(body)
    }

fn format_messages(&self, messages: &[Message]) -> (Option<String>, Vec<AnthropicMessage>) {
        let mut system_content: Option<String> = None;
        let mut api_messages: Vec<AnthropicMessage> = Vec::new();

        for msg in messages {
            let content = match &msg.content {
                MessageContent::Text(text) => text.clone(),
                MessageContent::Parts(parts) => {
                    // Concatenate text parts for now
                    parts
                        .iter()
                        .filter_map(|p| {
                            if let crate::agent::ContentPart::Text { text } = p {
                                Some(text.as_str())
                            } else {
                                None
                            }
                        })
                        .collect::<Vec<_>>()
                        .join("\n")
                }
            };

            match msg.role {
                MessageRole::System => {
                    // Anthropic requires system as a separate field
                    system_content = Some(content);
                }
                MessageRole::User => {
                    api_messages.push(AnthropicMessage {
                        role: "user".to_string(),
                        content,
                    });
                }
                MessageRole::Assistant => {
                    api_messages.push(AnthropicMessage {
                        role: "assistant".to_string(),
                        content,
                    });
                }
            }
        }

        (system_content, api_messages)
    }

fn format_messages_with_tools(
        &self,
        messages: &[Message],
    ) -> (Option<String>, Vec<AnthropicMessageWithContent>) {
        let mut system_content: Option<String> = None;
        let mut api_messages: Vec<AnthropicMessageWithContent> = Vec::new();

        for msg in messages {
            match msg.role {
                MessageRole::System => {
                    // Extract system content
                    if let MessageContent::Text(text) = &msg.content {
                        system_content = Some(text.clone());
                    }
                }
                MessageRole::User => {
                    let content = self.message_content_to_api(&msg.content);
                    api_messages.push(AnthropicMessageWithContent {
                        role: "user".to_string(),
                        content,
                    });
                }
                MessageRole::Assistant => {
                    let content = self.message_content_to_api(&msg.content);
                    api_messages.push(AnthropicMessageWithContent {
                        role: "assistant".to_string(),
                        content,
                    });
                }
            }
        }

        // OAuth mode requires the system prompt to start with Claude Code identifier
        if self.auth_mode == AuthMode::OAuth {
            let prefix = CLAUDE_CODE_PROMPT_PREFIX;
            system_content = Some(match system_content {
                Some(existing) => format!("{}\n\n{}", prefix, existing),
                None => prefix.to_string(),
            });
        }

        (system_content, api_messages)
    }

fn message_content_to_api(&self, content: &MessageContent) -> Vec<ApiContentBlock> {
        match content {
            MessageContent::Text(text) => {
                vec![ApiContentBlock::Text { text: text.clone() }]
            }
            MessageContent::Parts(parts) => parts
                .iter()
                .map(|part| match part {
                    ContentPart::Text { text } => ApiContentBlock::Text { text: text.clone() },
                    ContentPart::ToolUse { id, name, input } => ApiContentBlock::ToolUse {
                        id: id.clone(),
                        name: name.clone(),
                        input: input.clone(),
                    },
                    ContentPart::ToolResult {
                        tool_use_id,
                        content,
                        is_error,
                    } => ApiContentBlock::ToolResult {
                        tool_use_id: tool_use_id.clone(),
                        content: content.clone(),
                        is_error: Some(*is_error),
                    },
                })
                .collect(),
        }
    }

fn name(&self) -> &str {
        "claude"
    }

fn model(&self) -> &str {
        &self.model
    }

fn context_window(&self) -> usize {
        CONTEXT_WINDOW
    }

fn max_output_tokens(&self) -> usize {
        MAX_OUTPUT_TOKENS
    }

fn supports_caching(&self) -> bool {
        true // Claude supports prompt caching
    }

fn supports_streaming(&self) -> bool {
        false // Streaming will be implemented in PROV-002
    }

async fn complete(&self, messages: &[Message]) -> Result<String> {
        let (system, api_messages) = self.format_messages(messages);

        let request = AnthropicRequest {
            model: self.model.clone(),
            max_tokens: MAX_OUTPUT_TOKENS,
            system,
            messages: api_messages,
        };

        let body = self.send_api_request(&request).await?;

        let response: AnthropicResponse = serde_json::from_str(&body)
            .map_err(|e| anyhow!("Failed to parse response: {} - body: {}", e, body))?;

        // Extract text from content blocks
        let text = response
            .content
            .iter()
            .filter_map(|block| {
                if block.block_type == "text" {
                    block.text.as_ref()
                } else {
                    None
                }
            })
            .cloned()
            .collect::<Vec<_>>()
            .join("");

        Ok(text)
    }

async fn complete_with_tools(
        &self,
        messages: &[Message],
        tools: &[ToolDefinition],
    ) -> Result<CompletionResponse> {
        let (system, api_messages) = self.format_messages_with_tools(messages);

        // Convert tool definitions to API format
        let api_tools: Vec<ApiToolDefinition> = tools
            .iter()
            .map(|t| ApiToolDefinition {
                name: t.name.clone(),
                description: t.description.clone(),
                input_schema: t.input_schema.clone(),
            })
            .collect();

        let request = AnthropicRequestWithTools {
            model: self.model.clone(),
            max_tokens: MAX_OUTPUT_TOKENS,
            system,
            messages: api_messages,
            tools: if api_tools.is_empty() {
                None
            } else {
                Some(api_tools)
            },
        };

        let body = self.send_api_request(&request).await?;

        let response: AnthropicResponseWithTools = serde_json::from_str(&body)
            .map_err(|e| anyhow!("Failed to parse response: {} - body: {}", e, body))?;

        // Convert API response to internal format
        let content_parts: Vec<ContentPart> = response
            .content
            .iter()
            .map(|block| match block {
                ResponseContentBlock::Text { text } => ContentPart::Text { text: text.clone() },
                ResponseContentBlock::ToolUse { id, name, input } => ContentPart::ToolUse {
                    id: id.clone(),
                    name: name.clone(),
                    input: input.clone(),
                },
            })
            .collect();

        let stop_reason = match response.stop_reason.as_str() {
            "tool_use" => StopReason::ToolUse,
            "max_tokens" => StopReason::MaxTokens,
            _ => StopReason::EndTurn,
        };

        Ok(CompletionResponse {
            content: MessageContent::Parts(content_parts),
            stop_reason,
        })
    }
